+++ b&#x2F;packages&#x2F;server&#x2F;src&#x2F;adapters&#x2F;ws.ts
@@ -167,8 +167,6 @@ export function getWSConnectionHandler&amp;lt;TRouter extends AnyRouter&amp;gt;(
         (globalThis.setImmediate ?? globalThis.setTimeout)(() =&amp;gt; {
           client.close();
         });
-
-        throw error;
       });
